mapper.select:
SELECT f.account_id_from AS account_id_from, f.account_id_to AS account_id_to, a.account_id AS account_id, a.user_id AS user_id, a.user_name AS user_name, a.profile_photo_url AS profile_photo_url, a.shussha_count AS shussha_count, f.status AS status, f.lock_version AS lock_version, f.updated_at AS updated_at FROM account a INNER JOIN friend f ON a.account_id = f.account_id_to INNER JOIN shussha s ON a.account_id = s.account_id WHERE f.account_id_from = #{accountId} AND a.status != '${@net.akichil.shusshare.entity.AccountStatus@DELETED.getValue}'
AND #{startDate} <= date
AND date <= #{endDate}

mapper.insert:
INSERT INTO friend(
    account_id_from,
    account_id_to,
    status
)
VALUES(
    #{accountIdFrom},
    #{accountIdTo},
    #{status}
)

mapper.update:
UPDATE friend SET
status = #{status},
lock_version = lock_version + 1, updated_at = CURRENT_TIMESTAMP WHERE account_id_from = #{accountIdFrom} AND account_id_to = #{accountIdTo} AND lock_version = #{lockVersion}

mapper.delete:
DELETE FROM
    friend
WHERE
    account_id_from = #{accountIdFrom}
    AND
    account_id_to = #{accountIdTo}
    AND
    lock_version = #{lockVersion}
