mapper.select:
SELECT
FROM
WHERE (created_by = #{accountId} OR f.status = '${@net.akichil.shusshare.entity.FriendStatus@FOLLOWED.getValue}') AND r.status != '${@net.akichil.shusshare.entity.RecruitmentStatus@DELETED.getValue}'
AND r.created_by = #{createdById}
AND r.shussha_id = #{shusshaId}
AND #{startDate} <= s.date
AND s.date <= #{endDate}

mapper.insert:
INSERT INTO recruitment_participant (
    recruitment_id,
    account_id
) VALUES
 (
    #{recruitmentId},
    #{accountId}
)
